package Entity;public class EntityRecord {    private String entity;    private Float incomingSettlementAmount;    private Float outgoingSettlementAmount;    public EntityRecord() {    }    public EntityRecord(String entity, Float incomingSettlementAmount, Float outgoingSettlementAmount) {        this.entity = entity;        this.incomingSettlementAmount = incomingSettlementAmount;        this.outgoingSettlementAmount = outgoingSettlementAmount;    }    public String getEntity() {        return entity;    }    public void setEntity(String entity) {        this.entity = entity;    }    public Float getIncomingSettlementAmount() {        return incomingSettlementAmount;    }    public void setIncomingSettlementAmount(Float incomingSettlementAmount) {        this.incomingSettlementAmount = incomingSettlementAmount;    }    public Float getOutgoingSettlementAmount() {        return outgoingSettlementAmount;    }    public void setOutgoingSettlementAmount(Float outgoingSettlementAmount) {        this.outgoingSettlementAmount = outgoingSettlementAmount;    }}